jQuery(document).ready(function($){
	var htmlOutput = '<div id="enroute_settings_wrap" style="display: none;">'+
		'<h1>Settings</h1>'+
		'<table id="enroute_formtable">'+
			'<tr>'+
				'<td class="enroute_cellname">'+
					'Height (in pixels)'+
				'</td>'+
				'<td class="enroute_celloption">'+
					'<input id="enroute_height" type="number" value="400" min="1" />'+
				'</td>'+
			'</tr>'+
			'<tr>'+
				'<td colspan="3">'+
					'<button id="enroute_insertfaqs" class="button button-primary button-large">Insert Enroute</button>'+
					'<button id="enroute_insertcancel" class="button button-default button-large">Cancel</button>'+
				'</td>'+
			'</tr>'+
		'</table>'+
	'</div>';

	$('body').append( htmlOutput );

	$('#enroute_sbtn').click(function(e){
		tb_show( "Enroute Shortcode", "#TB_inline?width=753&height=550&inlineId=enroute_settings_wrap" );
		e.preventDefault();
	});

	$('#enroute_insertcancel').click(function(){
		tb_remove();
	});

	$('#enroute_insertfaqs').click(function(){
		var height = $('#enroute_height').val();

		var shortcodeOutput = '[enroute '+
		'height="'+ height +'"]';

		send_to_editor( shortcodeOutput );
		tb_remove();
	});
});
